    private void btnCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCadastrarActionPerformed
        try {
            if (textCpf.getText().isEmpty() || textData.getText().isEmpty() || textNome.getText().isEmpty() || textTelefone.getText().isEmpty()) {
                JOptionPane.showMessageDialog(rootPane, "Preencha todos os campos");
            }

            if (Validacoes.validarCpf(textCpf.getText()) == false) {
                throw new NumberFormatException("CPF invalido use o formato: 123.456.789-10");
            }

            if (Validacoes.validarData(textData.getText()) == false) {
                throw new NumberFormatException("DATA invalido use o formato: dd/mm/aaaa");
            }

            if (Validacoes.validarTelefone(textTelefone.getText()) == false) {
                throw new NumberFormatException("TELEFONE invalido use o formato:(XX) 9XXXX-XXXX ou (XX) XXXX-XXXX ");
            }

            inserirPaciente(getPaciente());

            textNome.setText("");
            textCpf.setText("");
            textTelefone.setText("");
            textData.setText("");
            cbPaciente.setSelected(false);
            dispose();

        } catch (RuntimeException e) {
            JOptionPane.showMessageDialog(null, e.getMessage());
        }
    }//GEN-LAST:event_btnCadastrarActionPerformed

