    public Consulta getPaciente() {
        Consulta paciente = new Consulta();
        paciente.setNomePaciente(textNome.getText());
        paciente.setCpfPaciente(textCpf.getText());
        paciente.setTelefonePaciente(textTelefone.getText());
        paciente.setDataConsulta(textData.getText());

        if (cbPaciente.isSelected()) {
            paciente.setJaEraPaciente("Sim");
        } else {
            paciente.setJaEraPaciente("Não");
        }
        paciente.setConsultaFinalizada("Não");

        return paciente;
    }

    public void inserirPaciente(Consulta paciente) {
        telaInicial.pacientesList.add(paciente);
        telaInicial.atualizarTabela();
    }

    public int posicaoPaciente() {
        int posicao = telaInicial.tblPacientes.getSelectedRow();
        if (posicao == -1) {
            JOptionPane.showMessageDialog(rootPane, "Selecione um Paciente");
        }
        return posicao;
    }

