    private void btnFinalizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnFinalizarActionPerformed
        try {
            if (textReceita.getText().isEmpty()) {
                throw new RuntimeException("O campo Receita e Observações está vazio");
            }
            Consulta consulta = telaInicial.pacientesList.get(telaInicial.posicaoPaciente());
            consulta.setReceita(textReceita.getText());
            telaInicial.atualizarTabela();
            
            dispose();
            
        } catch (RuntimeException e) {
            
            JOptionPane.showMessageDialog(rootPane, e);
        }
    }//GEN-LAST:event_btnFinalizarActionPerformed

    private void cbConsultaRealizadaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cbConsultaRealizadaActionPerformed
        if (cbConsultaRealizada.isSelected()) {
            
            Consulta consulta = telaInicial.pacientesList.get(telaInicial.posicaoPaciente());
            consulta.setConsultaFinalizada("Sim");
        }
    }//GEN-LAST:event_cbConsultaRealizadaActionPerformed

    private void textReceitaAncestorAdded(javax.swing.event.AncestorEvent evt) {//GEN-FIRST:event_textReceitaAncestorAdded
        Consulta consulta = telaInicial.pacientesList.get(telaInicial.posicaoPaciente());
        if (consulta.getConsultaFinalizada() == "Sim") {
            
            textReceita.setText(consulta.getReceita());
            textReceita.setEditable(false);
        }
    }//GEN-LAST:event_textReceitaAncestorAdded

