    public void mostrarTela(TelaInicial tela) {
        this.telaInicial = tela;
        setVisible(true);
    }

    public void marcarCheckBox() {  
        cbConsultaRealizada.setSelected(true);
    }
    
    public void desabilitarCheckBox(){
        cbConsultaRealizada.setEnabled(false);
    }

    public void esconderBotaoFinalizar() {
        btnFinalizar.setEnabled(false);
        btnFinalizar.setVisible(false);
    }
